// obj参数表示需要被深拷贝的对象function deepCopy(obj) &#123;    // 判断obj参数的数据类型是否为&quot;object&quot;    if (typeof obj !== &quot;object&quot;) &#123;        return obj;    &#125;    // 如果obj是对象,我们就创建新的空对象    // 如果obj是数组,我们就创建新的空数组    var newObj = Array.isArray(obj) ? [] : &#123;&#125;;    // 遍历obj    for (var attr in obj) &#123;        if (typeof obj[attr] !== &quot;object&quot;) &#123;            // 如果obj[attr]的数据类型是基本数据类型,可以直接赋值给newObj            newObj[attr] = obj[attr];        &#125; else &#123;            // 如果obj[attr]的数据类型是引用数据类型,需要再次进行deepCopy            newObj[attr] = deepCopy(obj[attr]);        &#125;    &#125;    // 返回newObj对象    return newObj;&#125;

