function mydebounce(func, wait) &#123;    // 延时器标识符    var timeoutId = null;    // 使用一个变量,保存this上下文的对象    var context = null;    // 使用一个变量,保存实参列表    var args = null;    // 返回一个新函数    return function () &#123;        // 把正确的this对象赋值给context变量        context = this;        // 把正确的arguments对象赋值给args变量        args = arguments;        console.log(arguments);        // 清除上一个延时器        window.clearTimeout(timeoutId);        // 开启一个延时器        timeoutId = window.setTimeout(function () &#123;            // 改变func函数内部this指向,并且传递参数            func.apply(context, args);        &#125;, wait);    &#125;&#125;// 获取dom对象var container = document.getElementById(&quot;container&quot;);// 定义一个变量,保存次数var count = 0;// 封装一个函数function fn(e) &#123;    // console.log(&quot;fn函数被调用了&quot;);    console.log(&quot;this对象=&gt;&quot;, this);    console.log(&quot;e事件对象=&gt;&quot;, e);    console.log(&quot;&quot;);    // // 自加1    count++;    // // 设置container标签内容    this.innerText = &quot;触发了&quot; + count + &quot;次事件,当前鼠标在盒子内坐标为&quot; + e.offsetX + &quot;,&quot; + e.offsetY;&#125;// 给container对象绑定鼠标移动事件// 使用自己封装的防抖动函数container.onmousemove = mydebounce(fn, 400);var objInput = document.querySelector(&quot;input&quot;);objInput.onkeyup = mydebounce(function (e) &#123;    console.log(&quot;this对象=&gt;&quot;, this);    console.log(&quot;e事件对象=&gt;&quot;, e);    console.log(&quot;e.key=&gt;&quot;, e.key);    console.log(this.value);&#125;, 400);

函数的节流
概念规定在一个单位时间内，只能触发一次函数。如果这个单位时间内触发多次函数，只有一次生效。

生活中例子举个栗子，坐火车或地铁或高铁，过安检的时候，在一定时间（例如10秒）内，只允许一个乘客通过安检入口，以配合安检人员完成安检工作。上例中，每10秒内，仅允许一位乘客通过，分析可知，“函数节流”的要点在于，在 一定时间 之内，限制 一个动作 只 执行一次 。
再举个栗子， 王者荣耀某个英雄释放技能以后,该技能进入冷却,等该技能冷却结束以后才能再次释放技能

函数节流的使用场景
DOM元素的拖拽功能实现
射击游戏
计算鼠标移动的距离
监听scroll滚动事件


函数节流的实现在函数的节流中首次触发是否执行以及结束后是否执行，不同需求，实现的方式也有所不同。目前实现函数节流有两种主流方式：

使用时间戳(从1970年1月1日0时0分0秒到现在的毫秒数)
设置延时器(setTimeout)。

我们用 leading 代表首次是否执行，trailing 代表结束后是否再执行一次。

使用时间戳完成节流函数的第一版本**原理：**当触发事件的时候，我们取出当前的时间戳，然后减去之前的时间戳(最一开始值设为 0 )，如果大于设置的时间周期(时间间隔)，就执行函数，并更新时间戳为当前的时间戳，如果小于，就不执行。**目标：**第一次触发,最后不会被调用触发函数


function mythrottle1(func, wait) &#123;    // 之前的时间戳    var old = 0;    // 定义一个变量保存this对象    var context = null;    // 定义一个变量保存实参列表    var args = null;    // 返回一个新函数    return function () &#123;        // 保存this对象        context = this;        // 保存arguments对象        args = arguments;        // 获取当前最近的时间戳        var now = new Date().getTime();        // 如果大于设置的时间周期(时间间隔)，就执行函数，如果小于，就不执行。        if (now - old &gt; wait) &#123;            // 修改函数内部this指向,并传递参数            func.apply(context, args);            // 并更新时间戳为当前的时间戳            old = now;        &#125;    &#125;&#125;


使用延时器完成节流函数的第二版本**原理：**当触发事件的时候，设置一个延时器，再触发事件的时候，如果延时器存在，就不执行，直到延时器执行完毕，然后执行函数，清空延时器，再设置下个延时器。**目标：**实现第二个版本  第一次不触发,最后一次触发

function mythrottle2(func, wait) &#123;    // 定义一个变量保存this对象    var context = null;    // 定义一个变量保存实参列表    var args = null;    // 延时器标识符    var timeoutId = null;    // 返回一个新函数    return function () &#123;        // 保存this对象        context = this;        // 保存arguments对象        args = arguments;        // 判断延时器是否存在        if (timeoutId === null) &#123; // 如果不延时器存在            // 开启延时器            timeoutId = window.setTimeout(function () &#123;                // 改变函数内部this指向以及传递参数                func.apply(context, args);                // 执行完毕函数以后, 重新给timeoutId标识符赋值为null                timeoutId = null;            &#125;, wait);        &#125;    &#125;&#125;


使用时间戳和延时器双剑合璧完成节流函数的第三版本**目标：**使用时间戳和延时器双剑合璧完成节流函数的第三个版本  第一次触发,最后一次也触发

function mythrottle3(func, wait) &#123;    // 之前的时间戳    var old = 0;    // 定义一个变量保存this对象    var context = null;    // 定义一个变量保存实参列表    var args = null;    // 延时器标识符    var timeoutId = null;    // 返回一个新函数    return function () &#123;        // 保存this对象        context = this;        // 保存arguments对象        args = arguments;        // 获取当前最近的时间戳        var now = new Date().getTime();        // 如果大于设置的时间周期(时间间隔)，就执行函数，如果小于，就不执行。        if (now - old &gt; wait) &#123;            // 修改函数内部this指向,并传递参数            func.apply(context, args)            // 并更新时间戳为当前的时间戳            old = now;            // 如果此时有延时器存在,则清除延时器,并且设置延时器变量timeout值恢复为null            if (timeoutId) &#123;                clearTimeout(timeoutId);                timeoutId = null;            &#125;        &#125;        // 判断延时器是否存在        if (timeoutId === null) &#123; // 如果不延时器存在            // 开启延时器            timeoutId = window.setTimeout(function () &#123;                // 改变函数内部this指向以及传递参数                func.apply(context, args);                // 执行完毕函数以后, 重新给timeoutId标识符赋值为null                timeoutId = null;                // 更新时间戳                // 重新获取现在最新的时间戳                now = new Date().getTime();                // 把now的值赋给old, 更新old的值                old = now;            &#125;, wait);        &#125;    &#125;&#125;function mythrottle(func, wait, options) &#123;    // 判断options是否为undefined    if (options === undefined || (options.leading &amp;&amp; options.trailing)) &#123;        return mythrottle3(func, wait);    &#125;    if (options.leading === true &amp;&amp; options.trailing === false) &#123;        return mythrottle1(func, wait);    &#125;    if (options.leading === false &amp;&amp; options.trailing === true) &#123;        return mythrottle2(func, wait);    &#125;&#125;

